// To parse this JSON data, do////     final user = userFromMap(jsonString);import 'package:meta/meta.dart';import 'dart:convert';class User {  User({    required this.database,    required this.usernameapi,    required this.passwordapi,  });  final String database;  final String usernameapi;  final String passwordapi;  factory User.fromJson(String str) => User.fromMap(json.decode(str));  String toJson() => json.encode(toMap());  factory User.fromMap(Map<String, dynamic> json) => User(    database: json["database"] == null ? null : json["database"],    usernameapi: json["usernameapi"] == null ? null : json["usernameapi"],    passwordapi: json["passwordapi"] == null ? null : json["passwordapi"],  );  Map<String, dynamic> toMap() => {    "database": database == null ? null : database,    "usernameapi": usernameapi == null ? null : usernameapi,    "passwordapi": passwordapi == null ? null : passwordapi,  };}