import 'package:flutter/material.dart';import 'package:form_field_validator/form_field_validator.dart';import 'package:shahwan/Homepage.dart';class LoginFormValidation extends StatefulWidget {  @override  _LoginFormValidationState createState() => _LoginFormValidationState();}class _LoginFormValidationState extends State<LoginFormValidation> {  GlobalKey<FormState> formkey = GlobalKey<FormState>();  String? validatePassword(String value) {    if (value.isEmpty) {      return "* Required";    } else if (value.length < 6) {      return "Password should be atleast 6 characters";    } else if (value.length > 15) {      return "Password should not be greater than 15 characters";    } else      return null;  }  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Color.fromRGBO(167, 53, 58 ,100),      appBar: AppBar(        title: Text("Login Page"),      ),      body: SingleChildScrollView(        child: Form(          autovalidate: true, //check for validation while typing          key: formkey,          child: Column(            children: <Widget>[              Padding(                padding: const EdgeInsets.only(top: 60.0),                child: Center(                  child: Container(                      width: 200,                      height: 150,                      child: Image.asset('asset/images/LOGO-01-3.png')),                ),              ),              Padding(                padding: EdgeInsets.symmetric(horizontal: 15),                child: TextFormField(                    decoration: InputDecoration(                        border: OutlineInputBorder(),                        labelText: 'اسم المستخدم',                        hintText: 'ادخل اسم المستخدم'),                    validator: MultiValidator([                      RequiredValidator(errorText: "* مطلوب"),                      EmailValidator(errorText: "ادخل اسم مستخدم صالح "),                    ])),              ),              Padding(                padding: const EdgeInsets.only(                    left: 15.0, right: 15.0, top: 15, bottom: 0),                child: TextFormField(                    obscureText: true,                    decoration: InputDecoration(                        border: OutlineInputBorder(),                        labelText: 'كلمة السر ',                        hintText: 'ادخل كلمة سر امنة '),                    validator: MultiValidator([                      RequiredValidator(errorText: "* مطلوب"),                      MinLengthValidator(6,                          errorText: "Password should be atleast 6 characters"),                      MaxLengthValidator(15,                          errorText:                          "Password should not be greater than 15 characters")                    ])                  //validatePassword,        //Function to check validation                ),              ),              // FlatButton(              //   onPressed: () {              //     //TODO FORGOT PASSWORD SCREEN GOES HERE              //   },              //   child: Text(              //     'Forgot Password',              //     style: TextStyle(color: Colors.blue, fontSize: 15),              //   ),              // ),              SizedBox(                height: 40,              ),              Container(                height: 50,                width: 250,                decoration: BoxDecoration(                    color: Colors.white,                    borderRadius: BorderRadius.circular(20)),                child: FlatButton(                  onPressed: () {                    if (formkey.currentState!.validate()) {                      Navigator.push(context,                          MaterialPageRoute(builder: (_) => HomePage()));                      print("تم");                    } else {                      print("خطاء في التسجيل");                    }                  },                  child: Text(                    'تسجيل دخول  ',                    style: TextStyle(color: Colors.black, fontSize: 25),                  ),                ),              ),              SizedBox(                height: 100,              ),            ],          ),        ),      ),    );  }}