import 'package:intl/intl.dart';import 'package:flutter/material.dart';import 'package:qrscan/qrscan.dart' as scanner;import 'package:flutter/services.dart';import 'package:shahwan/Services/service.dart';import 'package:shahwan/Model/ProductTabel.dart';import 'package:path_provider/path_provider.dart';import 'package:sqflite/sqflite.dart';import 'package:path/path.dart';import 'package:collection/collection.dart';import 'package:http/http.dart' as http;// import 'package:syncfusion_flutter_datagrid/datagrid.dart';import 'Database/sqlLite.dart';class QRViewExample extends StatefulWidget {  @override  _QRViewExampleState createState() => _QRViewExampleState();}class _QRViewExampleState extends State<QRViewExample> {  String result = "Hello Worlderer..!";  String Name = "Hello Worlderer..!";  var DataTabel1;  DatabaseHelper db = DatabaseHelper();  Future _scanQR() async {    try {      String? cameraScanResult = await scanner.scan();      print("cameraScanResult" + cameraScanResult.toString());      var cameraScanResultNumber = cameraScanResult.toString().substring(0,          cameraScanResult.toString().indexOf('|'));      var cameraScanResultName = cameraScanResult.toString().substring        (cameraScanResult.toString().indexOf('|\$\$|') + 4, cameraScanResult          .toString().indexOf('-'));      // var  code = cameraScanResult.toString().split(' ');      print("cameraScanResult>>>>>>>>" + cameraScanResultNumber.toString());      print("cameraScanResultName >>>>>>" + cameraScanResultName.toString());      DateTime now = DateTime.now();      String formattedDate = DateFormat('dd/MM/yyyy').format(now);      var name=cameraScanResultName;      var defaultCode = cameraScanResultNumber;      var productUomQty = '1 '.toString();       db.SaveProduct(new ProductTabel(name.toString(),          defaultCode.toString(),          productUomQty));      var all = await db.getProduct();      print ("ALL>>>>"+ all.toString() );      // var data = {      //   'database': 'shahwan',      //   'login': 'admin',      //   'OdooApi': 'sgsdsfdgfdbrberb',      //   'date': formattedDate,      //   'qrcode_sale_form_line_ids': DataTabel1.toString(),      // };      // print("data" + data.toString());      // var result1 = await Service("qrcode_receive").Post(data);      // print("result" + result1.toString());      setState(() {        result = cameraScanResultNumber.toString();        Name = cameraScanResultName.toString();        // setting string        // result with        // cameraScanResult      });    } on PlatformException catch (e) {      print(e);    }  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text(" المواصفات والكميات "),      ),      body: Center(          child: Column(              children: [              Text(Name.toString()),      Container(      ),      Text(result.toString()),      DataTable(        columns: const <DataColumn>[          DataColumn(            label: Text(              'الاسم',              style: TextStyle(fontStyle: FontStyle.italic),            ),          ),          DataColumn(            label: Text(              'الرقم',              style: TextStyle(fontStyle: FontStyle.italic),            ),          ),          DataColumn(            label: Text(              'الكمية',              style: TextStyle(fontStyle: FontStyle.italic),            ),          ),        ],        rows: const <DataRow>[          DataRow(            cells: <DataCell>[              DataCell(Text('Sarah')),              DataCell(Text('19')),              DataCell(Text('Student')),            ],          ),        ],      )      // Text(Data.toString()),      ],    ) // Here the scanned result will    // be shown    ),    floatingActionButton: FloatingActionButton.extended(    icon: Icon(Icons.camera_alt),    onPressed: () {    _scanQR();    // calling a function when user click on button    },    label: Text("Scan")),    floatingActionButtonLocation: FloatingActionButtonLocation.centerFloat,    );    }}abstract class oneScanne {  late final String default_code;  late final String Name;  late final String product_uom_qty;  // bool selected = false;  oneScanne(this.default_code, this.Name, this.product_uom_qty);}class Product {  final int index;  final String name;  final double price;  final int n;  Product(this.index, this.name, this.price, this.n);}// import 'dart:io';// import 'package:device_info/device_info.dart';// import 'package:flutter/foundation.dart';// import 'package:flutter/material.dart';// import 'package:qr_code_scanner/qr_code_scanner.dart';//////// class QRViewExample extends StatefulWidget {//   @override//   State<StatefulWidget> createState() => _QRViewExampleState();// }//// class _QRViewExampleState extends State<QRViewExample> {//   Barcode? result;//   QRViewController? controller;//   final GlobalKey qrKey = GlobalKey(debugLabel: 'QR');//   DeviceInfoPlugin deviceInfo = DeviceInfoPlugin();//////   // In order to get hot reload to work we need to pause the camera if the platform//   // is android, or resume the camera if the platform is iOS.//   @override//   void reassemble() {//     super.reassemble();//     if (Platform.isAndroid) {//       controller!.pauseCamera();//     }//     controller!.resumeCamera();//   }////   @override//   Widget build(BuildContext context) {//     return Scaffold(//       body: Column(//         children: <Widget>[//           Expanded(flex: 4, child: _buildQrView(context)),//           Expanded(//             flex: 1,//             child: FittedBox(//               fit: BoxFit.contain,//               child: Column(//                 mainAxisAlignment: MainAxisAlignment.spaceEvenly,//                 children: <Widget>[//                   if (result != null)//       